<template>
<div>
    <v-btn class="back_btn" @click="$router.go(-1)" icon>
      <v-icon>mdi-arrow-left</v-icon>
    </v-btn>
    <v-list two-line>
      <v-list-item>
        <v-list-item-avatar>
          <v-img
            src="data:image/png;base64,"></v-img>
        </v-list-item-avatar>
        <v-list-item-content>
          <v-list-item-title>{{ !!post.author ? $text.newText(post.author) : 'unknown' }}</v-list-item-title>
          <v-list-item-subtitle>{{ new Date($text.newText(post.pubDate)).toLocaleString() }}</v-list-item-subtitle>
        </v-list-item-content>
      </v-list-item>
    </v-list>
    <v-img
      v-if="!post.enclosure"
      height="250"
      :lazy-src="'https://cdn.shopify.com/s/files/1/0002/4680/8603/products/sjDBHUW_1600x1600.png?v=1606994170'"
      :src="'https://cdn.shopify.com/s/files/1/0002/4680/8603/products/sjDBHUW_1600x1600.png?v=1606994170'"
      aspect-ratio="1"
    >
      <template v-slot:placeholder>
        <v-row
          class="fill-height ma-0"
          align="center"
          justify="center"
        >
          <v-progress-circular
            indeterminate
            color="grey lighten-5"
          ></v-progress-circular>
        </v-row>
      </template>
    </v-img>
    <v-carousel
      v-else
      :continuous="false"
      :show-arrows="true"
      hide-delimiter-background
      delimiter-icon="mdi-minus"
      height="400"
    >
      <v-carousel-item
        v-for="(slide, i) in post.enclosure"
        :key="i"
      >
        <v-sheet
          height="100%"
          tile
        >
          <v-row
            class="fill-height"
            align="center"
            justify="center"
          >
            <div v-if="slide.$.type.includes('image')">
              <v-img
                height="400"
                :lazy-src="slide.$.url"
                :src="slide.$.url"
                aspect-ratio="1"
              >
                <template v-slot:placeholder>
                  <v-row
                    class="fill-height ma-0"
                    align="center"
                    justify="center"
                  >
                    <v-progress-circular
                      indeterminate
                      color="grey lighten-5"
                    ></v-progress-circular>
                  </v-row>
                </template>
              </v-img>
            </div>
            <div v-if="slide.$.type.includes('video')">
              <video height="300" controls>
                <source :src="slide.$.url" type="video/mp4">
              </video>
            </div>
          </v-row>
        </v-sheet>
      </v-carousel-item>
    </v-carousel>
    <h2>{{ $text.newText(post.title) }}</h2>
    <div>{{ $text.newText(post.description) }}</div>

</div>

</template>

<script>

export default {
  name: "posts-id",
  data() {
    return {
      post: {}
    }
  },
  mounted() {

    const post = this.$store.getters["posts/get_post"](this.$route.params.id);

    if (!post) {

      return this.$nuxt.error({ statusCode: 404, message: 'Post not found' });
    }
    this.post = post[0];
  }

}
</script>

<style scoped>
.back_btn {
  margin: 10px 0;
}
</style>
